import React from 'react';
import {Button, Icon, Ref} from "semantic-ui-react";
import './CommentsHeader.scss';

const chButton = React.forwardRef((props, ref) => (
  <div>
    <button basic compact icon labelPosition='left' />
  </div>
))

export function CommentsHeader(props) {
  const forwardedRef = React.useRef(null);

  return (
    <div className='comments-header'>
      <h4>{props.amountComments} Comments</h4>
      <Ref innerRef={forwardedRef}>
        <chButton>
          <Icon name='align left' />
          Sort by
        </chButton>
      </Ref>
    </div>
  );
}
