import React from 'react';
import SideBarItem from './SideBarItem/SideBarItem';
import {Menu, Divider} from 'semantic-ui-react';
import './SideBar.scss';
import {getVideosByCategory} from '../../store/reducers/videos';
import {SideBarHeader} from './SideBarHeader/SideBarHeader';
import {SideBarFooter} from './SideBarFooter/SideBarFooter';

export class SideBar extends React.Component {
  render() {
    const categoryGridTitles = this.getVideoTitlesForCategories();

    return (
      <Menu borderless vertical stackable fixed='left' className='side-nav'>
        <SideBarHeader/>
        <Divider/>
        <SideBarItem path='/' label='Home' icon='home'/>
        <SideBarItem path='/feed/trending' label='Trending' icon='fire'/>
        <Divider/>
        {categoryGridTitles}
        <SideBarFooter/>
      </Menu>
    );
  }

  getVideoTitlesForCategories() {
    const categoryTitles = Object.keys(this.props.videosByCategory || {});
    return categoryTitles.map((categoryTitle,index) => {
      return <SideBarItem path={'/feed/'+ categoryTitle +'trending'} label={categoryTitle} icon='fire'/>;
    });
  }
}

function mapStateToProps(state) {
  return {
    videosByCategory: getVideosByCategory(state)
  };
}
